package com.mohey.memberservice.controller;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.mohey.memberservice.dto.ResponseDto;import com.mohey.memberservice.dto.device.DeviceRegisterReqDto;import com.mohey.memberservice.dto.device.DeviceRegisterRespDto;import com.mohey.memberservice.service.MemberDeviceService;import lombok.RequiredArgsConstructor;@RequiredArgsConstructor@RequestMapping("/members/device")@RestControllerpublic class MemberDeviceController {	private final MemberDeviceService memberDeviceService;	@PostMapping("/register")	public ResponseEntity<?> register(@RequestBody DeviceRegisterReqDto deviceRegisterReqDto) {		DeviceRegisterRespDto deviceRegisterRespDto = memberDeviceService.registerDevice(deviceRegisterReqDto);		return new ResponseEntity<>(new ResponseDto<>(1, "기기등록 성공", deviceRegisterRespDto), HttpStatus.OK);	}}