package com.mohey.memberservice.controller;import java.util.ArrayList;import java.util.List;import javax.validation.Valid;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.mohey.memberservice.dto.ResponseDto;import com.mohey.memberservice.dto.memberalarm.TokenRespDto;import com.mohey.memberservice.dto.memberdevice.DeviceRegisterReqDto;import com.mohey.memberservice.dto.memberdevice.DeviceRegisterRespDto;import com.mohey.memberservice.service.MemberDeviceService;import lombok.RequiredArgsConstructor;@RequiredArgsConstructor@RequestMapping("/members/device")@RestControllerpublic class MemberDeviceController {	private final MemberDeviceService memberDeviceService;	@PostMapping("/register")	public ResponseEntity<?> register(@RequestBody @Valid DeviceRegisterReqDto deviceRegisterReqDto,		BindingResult bindingResult) {		DeviceRegisterRespDto deviceRegisterRespDto = memberDeviceService.registerDevice(deviceRegisterReqDto);		return new ResponseEntity<>(new ResponseDto<>(1, "기기등록 성공", deviceRegisterRespDto), HttpStatus.CREATED);	}	@GetMapping("/getToken/{sendUuid}/{receiverUuid}")	public ResponseEntity<?> getToken(@PathVariable String sendUuid, @PathVariable String receiverUuid) {		List<String> tokenList = new ArrayList<>();		TokenRespDto tokenRespDto = memberDeviceService.getToken(sendUuid, receiverUuid);		return new ResponseEntity<>(new ResponseDto<>(1, "기기등록 성공", tokenRespDto), HttpStatus.OK);	}}