package com.mohey.memberservice.controller;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.*;import com.mohey.memberservice.dto.ResponseDto;import com.mohey.memberservice.dto.memberdevice.DeviceRegisterReqDto;import com.mohey.memberservice.dto.memberdevice.DeviceRegisterRespDto;import com.mohey.memberservice.service.MemberDeviceService;import lombok.RequiredArgsConstructor;import javax.validation.Valid;import java.util.ArrayList;import java.util.List;@RequiredArgsConstructor@RequestMapping("/members/device")@RestControllerpublic class MemberDeviceController {	private final MemberDeviceService memberDeviceService;	@PostMapping("/register")	public ResponseEntity<?> register(@RequestBody @Valid DeviceRegisterReqDto deviceRegisterReqDto, BindingResult bindingResult) {		DeviceRegisterRespDto deviceRegisterRespDto = memberDeviceService.registerDevice(deviceRegisterReqDto);		return new ResponseEntity<>(new ResponseDto<>(1, "기기등록 성공", deviceRegisterRespDto), HttpStatus.CREATED);	}	@GetMapping("/getToken/{memberUuid}")	public ResponseEntity<?> getToken(@PathVariable String memberUuid) {		List<String> tokenList = new ArrayList<>();		tokenList = memberDeviceService.getToken(memberUuid);		return new ResponseEntity<>(new ResponseDto<>(1, "기기등록 성공",tokenList ), HttpStatus.OK);	}}