package com.mohey.memberservice.controller;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.mohey.memberservice.dto.ResponseDto;import com.mohey.memberservice.dto.memberInfo.getInfoRespDto;import com.mohey.memberservice.service.MemberInfoService;import lombok.RequiredArgsConstructor;@RequiredArgsConstructor@RequestMapping("/memberInfo")@RestControllerpublic class MemberInfoController {	private final MemberInfoService memberInfoService;	@PostMapping("/getInfo")	public ResponseEntity<?> join(@RequestBody String uuid) {		getInfoRespDto getInfoRespDto = memberInfoService.getInfo(uuid);		return new ResponseEntity<>(new ResponseDto<>(1, "", getInfoRespDto), HttpStatus.CREATED);	}}