package com.mohey.memberservice.controller;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.mohey.memberservice.dto.ResponseDto;import com.mohey.memberservice.dto.memberInfo.getInfoRespDto;import com.mohey.memberservice.service.MemberInfoService;import lombok.RequiredArgsConstructor;@RequiredArgsConstructor@RequestMapping("/memberInfo")@RestControllerpublic class MemberInfoController {	private final MemberInfoService memberInfoService;	@GetMapping("/{memberUuid}/getInfo")	public ResponseEntity<?> join(@PathVariable String memberUuid) {		getInfoRespDto getInfoRespDto = memberInfoService.getInfo(memberUuid);		return new ResponseEntity<>(new ResponseDto<>(1, "성공", getInfoRespDto), HttpStatus.OK);	}}