package com.mohey.memberservice.controller;import com.mohey.memberservice.dto.memberjoin.JoinReqDto;import com.mohey.memberservice.dto.memberjoin.JoinRespDto;import com.mohey.memberservice.dto.memberupdate.UpdateReqDto;import com.mohey.memberservice.dto.memberupdate.UpdateRespDto;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.*;import com.mohey.memberservice.dto.ResponseDto;import com.mohey.memberservice.dto.memberInfo.GetInfoRespDto;import com.mohey.memberservice.service.MemberInfoService;import lombok.RequiredArgsConstructor;import javax.validation.Valid;import java.util.ArrayList;import java.util.List;@RequiredArgsConstructor@RequestMapping("/members/info")@RestControllerpublic class MemberInfoController {	private final MemberInfoService memberInfoService;	@GetMapping("/{memberUuid}/getInfo")	public ResponseEntity<?> join(@PathVariable String memberUuid) {		GetInfoRespDto getInfoRespDto = memberInfoService.getInfo(memberUuid);		return new ResponseEntity<>(new ResponseDto<>(1, "성공", getInfoRespDto), HttpStatus.OK);	}	@GetMapping("/{memberUuid}/getInterest")	public ResponseEntity<?> getInterest(@PathVariable String memberUuid) {		List<String> interestList = new ArrayList<>();		interestList = memberInfoService.getInterest(memberUuid);		return new ResponseEntity<>(new ResponseDto<>(1, "성공", interestList), HttpStatus.OK);	}	@PostMapping("/updateInfo")	public ResponseEntity<?> join(@RequestBody @Valid UpdateReqDto updateReqDto, BindingResult bindingResult) {		UpdateRespDto updateRespDto = memberInfoService.updateInfo(updateReqDto);		return new ResponseEntity<>(new ResponseDto<>(1, "회원수정 성공", updateRespDto ), HttpStatus.CREATED);	}}