package com.mohey.memberservice.controller;import javax.validation.Valid;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.mohey.memberservice.dto.memberjoin.JoinReqDto;import com.mohey.memberservice.dto.memberjoin.JoinRespDto;import com.mohey.memberservice.dto.ResponseDto;import com.mohey.memberservice.service.MemberJoinService;import lombok.RequiredArgsConstructor;@RequiredArgsConstructor@RequestMapping("/members")@RestControllerpublic class MemberJoinController {	private final MemberJoinService memberJoinService;	@PostMapping("/join")	public ResponseEntity<?> join(@RequestBody @Valid JoinReqDto joinReqDto, BindingResult bindingResult) {		JoinRespDto joinRespDto = memberJoinService.register(joinReqDto);		return new ResponseEntity<>(new ResponseDto<>(1, "회원가입 성공", joinRespDto), HttpStatus.CREATED);	}}