package com.mohey.memberservice.dto.memberalarm;import java.util.List;import lombok.Builder;import lombok.Getter;import lombok.Setter;@Setter@Getterpublic class NotificationDto {	private String topic;	private String type;	private String senderUuid;	private String senderName;	private String receiverUuid;	private String receiverName;	private List<String> deviceTokenList;	@Builder	public NotificationDto(String topic, String type, String senderUuid, String senderName, String receiverUuid,		String receiverName, List<String> deviceTokenList) {		this.topic = topic;		this.type = type;		this.senderUuid = senderUuid;		this.senderName = senderName;		this.receiverUuid = receiverUuid;		this.receiverName = receiverName;		this.deviceTokenList = deviceTokenList;	}}