package com.mohey.memberservice.dto.memberdevice;import javax.validation.constraints.NotEmpty;import com.mohey.memberservice.domain.Member;import com.mohey.memberservice.domain.MemberDevice;import com.mohey.memberservice.domain.MemberDeviceAliveStatus;import com.mohey.memberservice.domain.MemberDeviceNotiStatus;import lombok.Getter;import lombok.Setter;@Setter@Getterpublic class DeviceRegisterReqDto {	@NotEmpty	private String memberUuid;	@NotEmpty	private String deviceUuid;	@NotEmpty	private String deviceToken;	public MemberDevice toMemberDeviceEntity(Member member) {		return MemberDevice.builder()			.deviceUuid(this.deviceUuid)			.deviceToken(this.deviceToken)			.memberId(member)			.build();	}	public MemberDeviceAliveStatus toMemberDeviceAliveStatusEntity(MemberDevice memberDevice) {		return MemberDeviceAliveStatus.builder()				.memberDeviceId(memberDevice)				.build();	}	public MemberDeviceNotiStatus toMemberDeviceNotiStatusEntity(MemberDevice memberDevice) {		return MemberDeviceNotiStatus.builder()				.memberDeviceId(memberDevice)				.build();	}}